INSERT INTO Modulo (ID, Nome, Descricao, Imagem) VALUES (1, 'Uma breve introdução', 'Nesse módulo é feito uma introdução a aspectos importantes da Libras', );
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'ABC', 'Nesse vídeo será abordado sobre a datilologia ou também conhecida como alfabeto dos surdos, teve uma das suas principais apresentações por volta de 1880, sendo demonstrado em panfletos com os sinais, porém não eram de ótima qualidade. Já em 1914 a relatos de panfletos com os sinais e a datilologia em ótima qualidade. Apesar dela ser apresentada nessas épocas, em estudos e visto que a datilologia teve seus primeiros passos na época da Grécia Antiga. Assista o vídeo e aprenda o alfabeto dos surdos, um passo necessário para então começar a aprender Libras.', 'Ramos, Clélia Regina. <bold>História da datilologia.</bold> (2005). Disponível em: < http://www.editora-arara-azul.com.br/site/ >. Acesso em: 02 set. 2018.', '<iframe width="560" height="315" src="https://www.youtube.com/embed/04I1Xj7Krh0?rel=0" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 1);
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'Parâmetros', 'Esse vídeo aborda sobre os parâmetros (configuração de mão, direcional idade, ponto de articulação movimento, localização, expressões faciais e corporais) em Libras que também podem ser morfemas, compõem sistemas complexos de desinências que estabelecem tipos de flexão verbais: concordância para gênero, para pessoa do discurso e para locativo, ou são afixos que se justapõem à raiz verbal ou nominal. Para o aprendizado de Libras, aprender os parâmetros se torna indispensável, assista o vídeo e saiba mais!', 'FELIPE, Tanya Amara. Os processos de formação de palavra na Libras. <bold>ETD-Educação Temática Digital</bold>, v. 7, n. 2, p. 200-217, 2006.', '<iframe width="560" height="315" src="https://www.youtube.com/embed/MldHJ02neEA?rel=0" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 1);
