INSERT INTO Modulo (ID, Nome, Descricao, Imagem) VALUES (2, 'Tempo', 'Nesse módulo é ensinado sobre períodos e dias da semana', );
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'Períodos e Dias da semana', 'Esse é um dos 2 vídeos do modulo que aborda sobre segundos, minutos, hora, dia, semana, mês, ano, século, milênio, ou seja, os períodos e tempo em geral. E demonstrado sinais tão importantes para a comunicação na hora de medir o tempo. O vídeo consiste apenas na demonstração de sinais, sendo a melhor forma de abordar o assunto ficando a critério do professor.', 'INÊS. <bold>AULA DE LIBRAS – PERÍODOS DE TEMPO</bold>. Disponível em: < http://tvines.org.br/?p=10332 >. Acesso em: 02 set. 2018.', '<iframe width="560" height="315" src="https://www.youtube.com/embed/hZtn0R-Cvec" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 2);
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'Dias da semana', 'Esse é um dos 2 vídeos do modulo que aborda sobre segundos, minutos, hora, dia, semana, mês, ano, século, milênio, ou seja, os períodos e tempo em geral. E demonstrado sinais tão importantes para a comunicação na hora de medir o tempo.', 'INÊS. <bold>AULA DE LIBRAS – PERÍODOS DE TEMPO</bold>. Disponível em: < http://tvines.org.br/?p=10332 >. Acesso em: 02 set. 2018. <br>INÊS. <bold>AULA DE LIBRAS – DIAS DA SEMANA</bold>. Disponível em: < https://www.youtube.com/watch?v=0BJ_NfumMUI >. Acesso em: 02 set. 2018.', '<iframe width="560" height="315" src="https://www.youtube.com/embed/uwJcGuR9hPI" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 2);
