INSERT INTO Modulo (ID, Nome, Descricao, Imagem) VALUES (3, 'Natureza', 'Nesse módulo é ensinado sobre Animais, Clima e a Natureza', );
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'Animais e Classificadores', 'Nesse vídeo é abordado sobre o vocabulário do nome de animais como “Leão”, “Tigre”, “Onça” entre outros. Também é abordado sobre um recurso essencial na Libras: O Classificadores. Os classificadores são sinais que resumem pensamentos, com a expressão corporal ele ajudam a resumir ideias e nomear coisas, eles também descrevem ações do corpo humano. Assistam o vídeo e entenda melhor sobre esses sinais!', 'FELIPE, Tanya Amara. Os processos de formação de palavra na Libras. <bold>ETD-Educação Temática Digital</bold>, v. 7, n. 2, p. 200-217, 2006.', '<iframe width="560" height="315" src="https://www.youtube.com/embed/cORmwVOz_5M" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 3);
INSERT INTO Video (ID, Nome, Descricao, Referencial, URL_Video, Modulo_FK) VALUES (NULL, 'Natureza, Animais, Clima e Estações', 'Esse vídeo aborda sobre questões da natureza em geral, principalmente nomenclaturas. É demonstrado sinais tão importantes para expressar animais, estações e climas, além de expressões como “Árvore”, “Mar” e “Rio”. O vídeo consiste apenas na demonstração de sinais, sendo a melhor forma de abordar o assunto ficando a critério do professor.', 'Não há referências', '<iframe width="560" height="315" src="https://www.youtube.com/embed/wlSE6ayEC8c" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', 3);
